let pills=[],numberPills=10;function setup(){cnv=createCanvas(windowWidth,windowHeight),cnv.parent("sketch-div-02"),rectMode(CENTER);for(let i=0;i<numberPills;i++){let t=random(-450,windowWidth+450),s=random(windowHeight/2,2*windowHeight),e=0,h=0,r=0,a=0;pills[i]=new pill1(t,s,e,h,r,a)}}function draw(){background(255,200,200);for(let i of pills)i.display(),i.ascend(),i.boarder()}class pill1{constructor(i,t,s,e,h,r){this.randX=i,this.randY=t,this.r1=random(0,255),this.r2=random(0,255),this.r3=random(0,255),this.rotateX=s,this.rotateR=random(-.002,.002),this.speed=random(.5,1),this.strokeWeight=r,this.strokeWeight1=75,this.strokeWeight2=85,this.strokeWeight3=100,this.sizeX=e,this.sizeY=h,this.sizeX2=int(random(1e3,1500)),this.sizeY2=int(random(1e3,1500)),this.mediaMatch1=[int(random(500,1e3)),int(random(500,1e3))],this.mediaMatch2=[int(random(500,2e3)),int(random(500,2e3))],this.mediaMatch3=[int(random(500,4e3)),int(random(500,4e3))]}display(){this.rotateX=this.rotateX+=this.rotateR,windowWidth>1e3?(this.sizeX=this.mediaMatch3[0],this.sizeY=this.mediaMatch3[1],this.strokeWeight=this.strokeWeight3):windowWidth>600?(this.sizeX=this.mediaMatch2[0],this.sizeY=this.mediaMatch2[1],this.strokeWeight=this.strokeWeight2):windowWidth>0&&(this.sizeX=this.mediaMatch1[0],this.sizeY=this.mediaMatch1[1],this.strokeWeight=this.strokeWeight1),this.sizeX&&this.sizeY>=1500&&(this.sizeX=this.sizeX2,this.sizeY=this.sizeY2),push(),translate(this.randX,this.randY),stroke(0),strokeWeight(this.strokeWeight),rotate(this.rotateX),fill(this.r1,this.r2,this.r3),rect(0,0,this.sizeX,this.sizeY,1e3),pop()}ascend(){this.randY=this.randY-this.speed}boarder(){this.randY<-2e3&&(this.randY=windowHeight+1e3,this.randX=this.randX)}}function windowResized(){resizeCanvas(windowWidth,windowHeight)}